import { FC, useEffect } from "react";
import { Routes, Route, useNavigate, useLocation } from "react-router-dom";
import { LogIn } from "../LogIn/LogIn";
import { CreateAccount } from "../CreateAccount/CreateAccount";
import { Home } from "../Home/Home";
import { Layout } from "../Layout/Layout";
import { ForgotPassword } from "../ForgotPassword/ForgotPassword";
import { ProtectedRoute } from "../ProtectedRoute/ProtectedRoute";
import { CreateProjectPage } from "../Home/CreateProjectPage/CreateProjectPage";
import { EmptyStateProject } from "../Home/EmptyStateProject/EmptyStateProject";
import { EmptyStateTask } from "../Home/EmptyStateTask/EmptyStateTask";
import { RootState } from "../../redux/store";
import { useAppDispatch, useAppSelector } from "../../redux/hooks";

export const App: FC = () => {
  const navigate = useNavigate();
  const location = useLocation();
  const dispatch = useAppDispatch();
  const isEmptyStateProject = useAppSelector(
    (state: RootState) => state.project.isEmptyStateProject
  );
  const isAuthenticated = useAppSelector(
    (state: RootState) => state.auth.isAuthenticated
  );
  useEffect(() => {
    if (isAuthenticated) {
      if (isEmptyStateProject) navigate("/empty_state_project");
      if (!isEmptyStateProject) navigate("/empty_state_task");
    }
  }, [isAuthenticated, isEmptyStateProject]);

  // useEffect(() => {
  //   if (isAuthenticated) {
  //     if (isEmptyStateProject) {
  //       navigate("/empty_state_project");
  //     } else {
  //       navigate("/");
  //     }
  //   }
  // }, [dispatch, isAuthenticated, isEmptyStateProject, location, navigate]);

  return (
    <Routes>
      <Route element={<ProtectedRoute />}>
        <Route element={<Layout />}>
          <Route element={<Home />} path="/" />
          <Route element={<CreateProjectPage />} path="/create_project" />
          <Route element={<EmptyStateProject />} path="/empty_state_project" />
          <Route element={<EmptyStateTask />} path="/empty_state_task" />
        </Route>
      </Route>
      <Route path="/login" element={<LogIn />} />
      <Route path="/create_account" element={<CreateAccount />} />
      <Route path="/forgot_password" element={<ForgotPassword />} />
    </Routes>
  );
};
