import { FC } from "react";
import { SearchInput } from "./SearchInput/SearchInput";
import { MenuToggle } from "./MenuToggle/MenuToggle";
import { useLocation } from "react-router-dom";
import { UserMenu } from "./UserMenu/UserMenu";
import { useAppSelector } from "../../redux/hooks";
import { RootState } from "../../redux/store";
import { MainTableNavigation } from "./MainTableNavigation/MainTableNavigation";
import { FaButtonPlus, FaEye, FaFilter, FaUsers } from "../icons/icons";

export const Navigation: FC = () => {
  const { pathname } = useLocation();
  const toggleIsActiveSidebar = useAppSelector(
    (state: RootState) => state.diff.isActiveSidebar
  );
  const exceptRoute =
    pathname === "/create_project" || pathname === "/empty_state_project";
  const shutdownEffect = exceptRoute && toggleIsActiveSidebar;
  const isMainRoute = pathname === "/main_table" || pathname === "/timeline";

  return (
    <div className="flex flex-col">
      <div
        className={`flex justify-start pr-4 py-2 border-b ${
          shutdownEffect ? "bg-gray-150 border-gray-300" : "bg-white"
        }
      `}
      >
        <MenuToggle />

        {isMainRoute && <MainTableNavigation />}

        <div className="flex w-full justify-end">
          <SearchInput />
          <UserMenu />
        </div>
      </div>
      {isMainRoute && (
        <div className="border py-2 flex items-center justify-between">
          <div className="flex">
            <button className="flex items-center rounded bg-primary-500 py-2 px-4">
              <FaButtonPlus />
              <span className="font-medium font-sm text-white pl-1">Task</span>
            </button>

            <button className="flex items-center">
              <FaEye />
              <span className="font-medium font-sm text-gray-600 pl-1 pr-4 border-r-[2px]">
                Show me
              </span>
            </button>

            <button className="flex items-center">
              <FaFilter />
              <span className="font-medium font-sm text-gray-600 pl-1 pr-4 border-r-[2px]">
                Filter
              </span>
            </button>
          </div>

          <div className="pr-4">
            <button className="flex items-center bg-primary-50 px-4 py-2 rounded">
              <FaUsers />
              <span className="font-medium font-sm text-primary-600 pl-1">
                Share
              </span>
            </button>
          </div>
        </div>
      )}
    </div>
  );
};
