import { FC, useRef, useState } from "react";
import { FaOpenMenu, FaCloseMenu, FaCheckCircle } from "../../icons/icons";
import { MenuToggleDropdown } from "./MenuToggleDropdown/MenuToggleDropdown";
import { useClickOutside } from "../../../hooks/useClickOutside";

export const MenuToggle: FC = () => {
  const [isOpen, setIsOpen] = useState<boolean>(false);
  const dropdownRef = useRef<HTMLDivElement>(null);
  useClickOutside(dropdownRef, () => setIsOpen(false));

  return (
    <div className={`flex w-auto flex-col`} ref={dropdownRef}>
      <div className={`z-[100] flex items-center`}>
        {isOpen ? (
          <div className="flex items-center justify-between w-80">
            <div className="flex items-center">
              <FaCheckCircle />
              <span className="text-sm font-bold uppercase tracking-[0.08em]">
                saas-mvp
              </span>
            </div>

            <button onClick={() => setIsOpen(!isOpen)}>
              <FaCloseMenu />
            </button>
          </div>
        ) : (
          <div>
            <button onClick={() => setIsOpen(!isOpen)} className="pl-4">
              <FaOpenMenu />
            </button>
          </div>
        )}
        {/* <button
          onClick={() => setIsOpen(!isOpen)}
          className={`${isOpen ? "ml-[220px]" : "pl-4"}`}
        >
          {isOpen ? <FaCloseMenu /> : <FaOpenMenu />}
        </button> */}
      </div>

      <MenuToggleDropdown isOpen={isOpen} />
    </div>
  );
};
