import { FC, useState } from "react";
import { FaDropdownSettings } from "../../../../icons/icons";
import { SwitchShowOnlyUnread } from "../SwitchShowOnlyUnread/SwitchShowOnlyUnread";

interface IProps {
  isOpen: boolean;
}
export const AppNotificationDropdown: FC<IProps> = ({ isOpen }) => {
  const [isChecked, setIsChecked] = useState<boolean>(false);

  const handleChange = (checked: boolean) => {
    setIsChecked(checked);
  };

  return (
    <div
      className={`absolute flex-col top-16 px-4 pt-4 pb-3 bg-white right-2 border rounded-lg w-[640px] h-[300px] ${
        isOpen ? "flex" : "hidden"
      }`}
    >
      <div className="flex justify-between">
        <div>
          <span className="text-lg font-medium">Notifications</span>
        </div>
        <div className="flex items-center">
          <span className="text-sm text-gray-600 font-normal">
            Show only unread
          </span>
          <SwitchShowOnlyUnread
            id={"example-switch"}
            label="Toggle Option"
            checked={isChecked}
            onChange={handleChange}
          />
          <button>
            <FaDropdownSettings />
          </button>
        </div>
      </div>
    </div>
  );
};
